import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

  const maxClock= 3000;
  const numberOfLayer = 19;
  const imgLayerPrefix = "./images/multilevelimage000L";
  const imgLayerFileExtencions = ".png";

  class Layer extends React.Component{
    constructor(props){
      super(props);
      this.state.imgNumber = props.imgNumber;
      this.state.imgLevel = props.imgLevel;
    }

    render(){
      //let randomNumber = Math.floor(Math.random()*numberOfLayer);
      return <div>
        <img class = "imgLayer"
          src= {
            imgLayerPrefix + this.props.imgNumber
            + imgLayerFileExtencions }
            style={"z-index:"+ this.state.imgLayer}
        />
        </div>;
    }
  }

  class LayerCollector extends React.Component{

    constructor(){
      this.state = {layers:[], nLayer:0};
    }

    render(){
      let leyers= this.state.layers;
      if(layers.lenght == 0){
        return null;
      }
      return layers.map((imageNumber, key) => <Layer imgNumber={imageNumber} imgLevel={key}/>);
    }

    componentWillUnmount(){
      while (true) {
        setTimeout(function() {
          let layers = this.state.layers;
          leyers.push(Math.floor(Math.random() * numberOfLayer));
          this.setState({nLayer:this.state.nLayer + 1, layers})
        }, Math.floor(Math.random() * maxClock))
      }
    }

  }

  ReactDOM.render(
  <LayerCollector />,
  document.getElementById('root')
);
