        setTimeout(function() {
          let layers = this.state.layers;
          layers.push(Math.floor(Math.random() * numberOfLayer));
          this.setState({nLayer:this.state.nLayer + 1, layers});
          console.log("LayerCollector componentWillMount 2");
        }, Math.floor(Math.random() * maxClock))
      }*/
    }

  }

  ReactDOM.render(
  <LayerCollector />,
  document.getElementById('root')
);
